using System.Collections;using System.Collections.Generic;using UnityEngine;public class GolfBall : MonoBehaviour{    public const int golfHitUIBitmask = 1 << 10;    [Header("Physics Properties")]    [SerializeField] private float maxVelocity = 30f;    [Header("Control Properties")]     private float maxPixelDrag = 10f;    [Header("Game Object Properties")]    [SerializeField] private GolfBallUI hitUI;    // Local variables    private Vector3 velocity;    private Vector3 clickStart;    private Vector3 proposedHit;    private Vector3 startHit;    private InteractionState state;    private InteractionState previousState;    public static GolfBall instance;    enum InteractionState    {        Ready,        Aiming,        Moving,        Paused,        Win,    }    private void Awake()    {        instance = this;        velocity = new Vector3();        state = InteractionState.Ready;        startHit = transform.position;    }    private Vector3 CalculateProposedHit(Vector3 mouseStart, Vector3 mouseCurr)    {        Debug.Log((mouseCurr - mouseStart));        // Rotate due to camera angle        Vector3 projectedMouseDrag = (mouseCurr - mouseStart);        // Cap the amount that the hit can be dragged        if (projectedMouseDrag.magnitude > maxPixelDrag)        {            projectedMouseDrag = projectedMouseDrag.normalized * maxPixelDrag;        }        float hitStrength = projectedMouseDrag.magnitude / maxPixelDrag;        Vector3 proposedVelocity = projectedMouseDrag.normalized * hitStrength * maxVelocity;        return proposedVelocity;    }    private void UpdateHitUI(Vector3 proposedHit)    {        hitUI.IsAiming(state == InteractionState.Aiming);        if (state != InteractionState.Aiming)        {            return;        }        Vector3 hitDirection = new Vector3(proposedHit.normalized.x, proposedHit.normalized.z, 0.0f);        float hitStrength = proposedHit.magnitude / maxVelocity;        hitUI.SetIndicator(hitDirection, hitStrength);    }    private void HandleInput()    {        if (state == InteractionState.Win)
        {
            return;
        }        if (Input.GetKeyDown(KeyCode.Escape)) {
            if (Time.timeScale > 0.0f)
            {
                Time.timeScale = 0;
                previousState = state;
                state = InteractionState.Paused;
                WinUIManager.instance.Pause();
            }
            else
            {
                Time.timeScale = 1;
                WinUIManager.instance.Unpause();
                state = previousState;
            }
        }
            if (state != InteractionState.Ready && state != InteractionState.Aiming)        {            return;        }        if (Input.GetMouseButtonDown(0) && state == InteractionState.Ready)        {            clickStart = Input.mousePosition;            state = InteractionState.Aiming;        }        if (state != InteractionState.Aiming)        {            return;        }        if (Input.GetMouseButton(0))        {
            RaycastHit raycastHit;
            Ray ray = Camera.main.ScreenPointToRay(Input.mousePosition);
            if (Physics.Raycast(ray, out raycastHit, Mathf.Infinity, GolfBall.golfHitUIBitmask))
            {
                proposedHit = CalculateProposedHit(transform.position, raycastHit.point);
            }        } else {            state = InteractionState.Moving;            velocity.x = -proposedHit.x;            velocity.z = -proposedHit.z;            GameManager.instance.HitBall();            SoundManager.instance.BallHit();        }    }    private void ApplyMotion()    {    }    private Ground GetGround()    {        RaycastHit hit;        // Does the ray intersect any objects excluding the player layer        if (Physics.Raycast(transform.position, transform.TransformDirection(Vector3.down), out hit, Mathf.Infinity, Ground.groundBitMask))        {            return hit.collider.gameObject.GetComponent<Ground>();        }        return null;    }    private void ApplyGroundEffect()    {        if (state != InteractionState.Moving)        {            return;        }        Ground ground = GetGround();        if (ground == null)        {            Debug.LogError("No ground!");            return;        }

        if (ground.HasEffect())        {            ground.Effect(this);        }        if (ground.HasFriction())        {            Vector3 frictionVelocityDelta = ground.Friction(velocity) * Time.deltaTime;            if (velocity == Vector3.zero || frictionVelocityDelta.magnitude > velocity.magnitude)            {                SetupBall(transform.position);            }            else            {                velocity += frictionVelocityDelta;            }        }        if (ground.HasForce())        {            velocity += ground.Force() * Time.deltaTime;        }        GetComponent<Rigidbody>().velocity = velocity;    }    public void ResetBall()    {        SetupBall(startHit);    }    public void SetupBall(Vector3 position)    {        transform.position = position;        velocity = new Vector3();        state = InteractionState.Ready;        startHit = transform.position;    }    void Update()
    {        HandleInput();        UpdateHitUI(proposedHit);
    }    void FixedUpdate()    {        ApplyMotion();        ApplyGroundEffect();    }    private void OnCollisionEnter(Collision collision)    {        if (collision.gameObject.CompareTag("Wall"))        {            Vector3 originalVelocity = velocity;            velocity = collision.gameObject.GetComponent<Wall>().BallVelocity(velocity, collision);
            SoundManager.instance.WallHit((velocity - originalVelocity).magnitude / (2 * maxVelocity));            GetComponent<Rigidbody>().velocity = velocity;            state = InteractionState.Moving;        }    }    private void OnTriggerStay(Collider other)    {        if (other.gameObject.CompareTag("Goal"))        {            if (state == InteractionState.Ready)            {                state = InteractionState.Win;                GetComponent<Collider>().enabled = false;                GetComponent<Rigidbody>().constraints = RigidbodyConstraints.FreezeRotation;                GetComponent<Rigidbody>().useGravity = true;                Vector3 goalPosition = other.gameObject.transform.position;                float gravity = -60;                float jumpVelocity = 15;                float time = -2 * jumpVelocity / gravity;                Vector3 dir = ( other.gameObject.transform.position - transform.position);                dir.y = 0;                dir = dir.normalized / (2 * time);                Physics.gravity = new Vector3(0, gravity, 0);                GetComponent<Rigidbody>().velocity = new Vector3(dir.x, jumpVelocity, dir.z);                StartCoroutine(TriggerWin(time));            }        }    }
    private IEnumerator TriggerWin(float waitTime)
    {
        yield return new WaitForSeconds(waitTime);
        SoundManager.instance.InHole();
        GameManager.instance.Win();
    }}